mapper.select:
SELECT A.*, B.`level` FROM `km_product` AS A
JOIN `km_member_seller` AS B ON A.`seller` = B.`uid`
WHERE `prodCate1`=? and `prodCate2`=? ORDER BY `sold` DESC LIMIT ?, 10;

SELECT A.*, B.`level` FROM `km_product` AS A
JOIN `km_member_seller` AS B ON A.`seller` = B.`uid`
WHERE `prodCate1`=? and `prodCate2`=? ORDER BY `sellPrice` ASC LIMIT ?, 10;

